CREATE TABLE QUESTION (ID BIGINT NOT NULL, EXAM_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE USER (ID BIGINT NOT NULL, EMAIL VARCHAR(255), FIRSTNAME VARCHAR(255), LASTNAME VARCHAR(255), PHONENUMBER VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE ORGANIZATIONALUNIT (ID BIGINT AUTO_INCREMENT NOT NULL, unitType VARCHAR(31), CREATEDODATE DATE, NAME VARCHAR(255), ORGANIZATION_ID BIGINT, CREATEDONDATE DATE, CREATEDBY_ID BIGINT, CODE VARCHAR(255), DEPARTMENT_ID BIGINT, DURATION LONGBLOB, STARTTIME TIME, COURSE_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE EXAMTAKEN (ID BIGINT NOT NULL, EXAM_ID BIGINT, TAKER_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE CHOISE (ID BIGINT NOT NULL, CHOISE VARCHAR(255), CORRECT TINYINT(1) default 0, QUESTION_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE USERCREDENTIAL (HASHEDPASSWORD VARCHAR(255), USER_ID BIGINT NOT NULL, PRIMARY KEY (USER_ID))
CREATE TABLE ANSWER (ID BIGINT NOT NULL, ANSERDBY_ID BIGINT, CHOOSED_ID BIGINT, FORQUESTION_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE EXAMTAKEN_ANSWER (ExamTaken_ID BIGINT NOT NULL, answers_ID BIGINT NOT NULL, PRIMARY KEY (ExamTaken_ID, answers_ID))
ALTER TABLE QUESTION ADD CONSTRAINT FK_QUESTION_EXAM_ID FOREIGN KEY (EXAM_ID) REFERENCES ORGANIZATIONALUNIT (ID)
ALTER TABLE ORGANIZATIONALUNIT ADD CONSTRAINT FK_ORGANIZATIONALUNIT_DEPARTMENT_ID FOREIGN KEY (DEPARTMENT_ID) REFERENCES ORGANIZATIONALUNIT (ID)
ALTER TABLE ORGANIZATIONALUNIT ADD CONSTRAINT FK_ORGANIZATIONALUNIT_ORGANIZATION_ID FOREIGN KEY (ORGANIZATION_ID) REFERENCES ORGANIZATIONALUNIT (ID)
ALTER TABLE ORGANIZATIONALUNIT ADD CONSTRAINT FK_ORGANIZATIONALUNIT_COURSE_ID FOREIGN KEY (COURSE_ID) REFERENCES ORGANIZATIONALUNIT (ID)
ALTER TABLE ORGANIZATIONALUNIT ADD CONSTRAINT FK_ORGANIZATIONALUNIT_CREATEDBY_ID FOREIGN KEY (CREATEDBY_ID) REFERENCES USER (ID)
ALTER TABLE EXAMTAKEN ADD CONSTRAINT FK_EXAMTAKEN_EXAM_ID FOREIGN KEY (EXAM_ID) REFERENCES ORGANIZATIONALUNIT (ID)
ALTER TABLE EXAMTAKEN ADD CONSTRAINT FK_EXAMTAKEN_TAKER_ID FOREIGN KEY (TAKER_ID) REFERENCES USER (ID)
ALTER TABLE CHOISE ADD CONSTRAINT FK_CHOISE_QUESTION_ID FOREIGN KEY (QUESTION_ID) REFERENCES QUESTION (ID)
ALTER TABLE USERCREDENTIAL ADD CONSTRAINT FK_USERCREDENTIAL_USER_ID FOREIGN KEY (USER_ID) REFERENCES USER (ID)
ALTER TABLE ANSWER ADD CONSTRAINT FK_ANSWER_FORQUESTION_ID FOREIGN KEY (FORQUESTION_ID) REFERENCES QUESTION (ID)
ALTER TABLE ANSWER ADD CONSTRAINT FK_ANSWER_ANSERDBY_ID FOREIGN KEY (ANSERDBY_ID) REFERENCES USER (ID)
ALTER TABLE ANSWER ADD CONSTRAINT FK_ANSWER_CHOOSED_ID FOREIGN KEY (CHOOSED_ID) REFERENCES CHOISE (ID)
ALTER TABLE EXAMTAKEN_ANSWER ADD CONSTRAINT FK_EXAMTAKEN_ANSWER_ExamTaken_ID FOREIGN KEY (ExamTaken_ID) REFERENCES EXAMTAKEN (ID)
ALTER TABLE EXAMTAKEN_ANSWER ADD CONSTRAINT FK_EXAMTAKEN_ANSWER_answers_ID FOREIGN KEY (answers_ID) REFERENCES ANSWER (ID)
CREATE TABLE SEQUENCE (SEQ_NAME VARCHAR(50) NOT NULL, SEQ_COUNT DECIMAL(38), PRIMARY KEY (SEQ_NAME))
